document.addEventListener("DOMContentLoaded",(()=>{console.log("Filter function loaded");const e=document.querySelectorAll(".hoobFilter__sectionTop");for(let t=0;t<e.length;t++)e[t].addEventListener("click",(function(e){const t=this.parentElement.querySelector(".hoobFilter__sectionInner");e.preventDefault(),t.classList.toggle("hoobFilter__sectionInner--close"),this.classList.toggle("hoobFilter__sectionTop--close")}))}));